mapper.select:
SELECT 

FROM wxbinghu.dbo.stz_region_apply_project_info a


	a.PROJECT_CODE IN (
	SELECT PROJECT_CODE
	FROM wxbinghu.dbo.STZ_REGION_APPROVE_ITEM_INFO
	WHERE CURRENT_STATE IN (
	'A0501'
	,'A0502'
	,'A0503'
	)
	) AND (p.major_flag IS NULL OR p.major_flag = 'Y')

		AND a.project_code = #{projectCode}


		AND a.project_name like
		'%'||#{projectName}||'%'
		'%'+#{projectName}+'%'
		CONCAT('%', #{projectName}, '%')


		AND a.total_money >= #{totalMoney}


		AND b.ENTERPRISE_NAME = #{enterpriseName}


		AND i.PrjNum IS NULL


		AND convert(VARCHAR(10), a.APPLY_DATE, 120) >= convert(VARCHAR(10), #{applyDateStart}, 120)


		AND convert(VARCHAR(10), a.APPLY_DATE, 120) <= convert(VARCHAR(10), #{applyDateEnd}, 120)


		AND a.PROJECT_NATURE = #{projectNature}


		AND a.PROJECT_TYPE in

			#{pt}





		ORDER BY ${page.orderBy}


		ORDER BY APPLY_DATE DESC

SELECT a.[ID] AS id ,a.[PROJECT_CODE] AS projectCode ,a.[PROJECT_NAME] AS projectName ,a.[PROJECT_TYPE] AS projectType ,a.[PROJECT_NATURE] AS projectNature FROM wxbinghu.dbo.stz_region_apply_project_info a LEFT JOIN MajorProjectLib.dbo.TBProjectInfo i on a.PROJECT_CODE = i.PROJECT_CODE
a.PROJECT_CODE IN ( SELECT PROJECT_CODE FROM wxbinghu.dbo.STZ_REGION_APPROVE_ITEM_INFO WHERE CURRENT_STATE IN ( 'A0501' ,'A0502' ,'A0503') ) AND i.PROJECT_CODE IS NULL
AND a.total_money >= #{totalMoney}
