mapper.select:
SELECT 

FROM wxbinghu.dbo.stz_region_approve_item_info a
WHERE a.id = #{id}

SELECT 

FROM [wxbinghu].[dbo].[STZ_REGION_APPLY_PROJECT_INFO] a


	c.ITEM_NAME IN ( SELECT approve_item_label
			FROM [MajorProjectLib].[dbo].[pub_approve_guide]
	)

		AND a.project_code = #{projectCode}


		AND a.PROJECT_NAME = #{projectName}


		AND c.ITEM_NAME = #{itemName}


		AND c.dept_code = #{deptCode}




		ORDER BY ${page.orderBy}


		ORDER BY b.DEAL_TIME DESC

SELECT 

FROM wxbinghu.dbo.stz_region_approve_item_info a






		ORDER BY ${page.orderBy}
