mapper.select:
SELECT 

FROM pub_expropriation a

WHERE a.id = #{id}

SELECT

FROM pub_expropriation a

WHERE a.lcid = #{lcid} AND a.town = #{town} AND a.village = #{village}

SELECT
FROM pub_expropriation a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.prj_name LIKE '%'||#{prjName}||'%' '%'+#{prjName}+'%' concat('%',#{prjName},'%')
AND a.town = #{town}
AND a.village = #{village}
AND a.approve_num LIKE '%'||#{approveNum}||'%' '%'+#{approveNum}+'%' concat('%',#{approveNum},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM pub_expropriation a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pub_expropriation SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
