mapper.select:
SELECT 

FROM pub_expropriation_detail_info a

WHERE a.id = #{id}

SELECT 

FROM pub_expropriation_detail_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.lcid = #{lcid}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM pub_expropriation_detail_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE pub_expropriation_detail_info SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE lcid = #{lcid}
