mapper.select:
SELECT 

FROM pub_trade a

WHERE a.id = #{id}

SELECT

FROM pub_trade a


	1 = 1

		AND a.indexId = #{indexid}


		AND a.title = #{title}

SELECT 

FROM pub_trade a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.tender_inner_num = #{tenderInnerNum}


		AND a.tender_type = #{tenderType}


		AND a.indexId LIKE
		'%'||#{indexid}||'%'
		'%'+#{indexid}+'%'
		concat('%',#{indexid},'%')


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.writetime = #{writetime}


		AND a.opentime = #{opentime}




		ORDER BY ${page.orderBy}


		ORDER BY a.openTime DESC

SELECT 

FROM pub_trade a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.openTime DESC

mapper.update:
UPDATE pub_trade SET
prj_code = #{prjCode},
prj_name = #{prjName},
tender_inner_num = #{tenderInnerNum},
tender_type = #{tenderType}
WHERE id = #{id}

UPDATE pub_trade SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
